CREATE TABLE tb_projeto(id_projeto INTEGER PRIMARY KEY AUTOINCREMENT,titulo TEXT NOT NULL,descricao TEXT NOT NULL,gerente TEXT NOT NULL, totalRequisitos INTEGER NULL DEFAULT 1);
CREATE TABLE tb_requisitos(id_requisito INTEGER NOT NULL,status TEXT NOT NULL,titulo TEXT NOT NULL,descricao TEXT NOT NULL,dataCricacao DATE NOT NULL ,dataModificacao DATE NULL,requerente TEXT NOT NULL,tb_projeto_id_projeto INTEGER NOT NULL,PRIMARY KEY (id_requisito, tb_projeto_id_projeto),FOREIGN KEY (tb_projeto_id_projeto) REFERENCES tb_projeto(id_projeto));
CREATE TABLE tb_requisitos_has_tb_requisitos(tb_requisitos_id_requisito INTEGER NOT NULL,tb_requisitos_tb_projeto_id_projeto INTEGER NOT NULL,tb_requisitos_id_dependente INT NOT NULL,PRIMARY KEY (tb_requisitos_id_requisito, tb_requisitos_tb_projeto_id_projeto, tb_requisitos_id_dependente), FOREIGN KEY (tb_requisitos_id_requisito, tb_requisitos_tb_projeto_id_projeto) REFERENCES tb_requisitos (id_requisito, tb_projeto_id_projeto), FOREIGN KEY (tb_requisitos_id_dependente) REFERENCES tb_requisitos(id_requisito));